        and age = #{bean.age}


        and sex = #{bean.sex}


        and phone = #{bean.phone}


        and email = #{bean.email}


        and address = #{bean.address}


        and reg_date = #{bean.regDate}


        and last_login_date = #{bean.lastLoginDate}


limit #{offset}, #{limit}

select
id, account, password, pickName, age, sex, phone, email, address, reg_date, last_login_date
from florist.user


        and id = #{bean.id}


        and account like concat('%', #{bean.account},'%')


        and password = #{bean.password}


        and pickName like concat('%',#{bean.pickName},'%')


        and age = #{bean.age}


        and sex like concat('%', #{bean.sex},'%')


        and phone = #{bean.phone}


        and email like concat('%', #{bean.email},'%')


        and address like concat('%', #{bean.address},'%')


        and reg_date = #{bean.regDate}


        and last_login_date = #{bean.lastLoginDate}

select  *
from florist.user


        and account = #{account}


        and password = #{password}


limit 1;
